import React, { useEffect, useState } from "react";
import Header from "../Components/Header/Header";
import { Card, List, Image, Typography, Select, Button } from "antd"
import axios from "axios";
const { Title, Paragraph } = Typography;

const img_fallback = "data:image/png;base64,";

//Styles
const body_div = {
  postion: "absolute",
  width: "100vw",
  height: "100vh",
  background: "#F5F5F5FF",
  display: "flex",
  flexDirection: "column",
}

const prizes_block = {
  flexGrow: 1,
  padding: "2%",
  display: "flex",
  flexWrap: "Wrap",
  justifyContent: "space-around",
  rowGap: "20px",
  overflowY: "scroll"
}

const full_width = {
  width: "100%"
}

const card_body = {
  display: "flex",
  gap: "2%",
}

const image = {
  minWidth: "200px",
  minHeight: "200px",
  objectFit: "cover",
  borderRadius: "10px"
}

const sort_block = {
  width: "100%",
  display: "flex",
  background: "white",
  borderRadius: "5px",
  justifyContent: "space-between",
  alignItems: "center",
  gap: "20px",
  flexWrap: "wrap",
  padding: "20px"
}


function MyPrizes() {

  const [filterValue, setFilterValue] = useState("0");
  const [sortValue, setSortValue] = useState("alphabet");
  const prize_list = "http://127.0.0.1:8000/api/prize_list";
  const access_token = (localStorage.getItem("accessToken") || "");
  const headers = {
    'Content-Type': 'application/json',
    'Authorization': 'Bearer ' + access_token,
  };

  const [data, setData] = useState([])

  const [filter_options, setFilter_options] = useState([])
  useEffect(() => {
    // Assuming prize_list_with_game is your endpoint that returns the modified data
    const params = {
      'sortValue': sortValue,
      'filterValue': filterValue
    }
    axios.get(prize_list, { headers, params }).then(response => {
      setData(response.data.prize_list_with_game);
      setFilter_options(response.data.organization)
    });
  }, []);


  const refresh = () => {
    console.log(filterValue);
    console.log(sortValue);
    const params = {
      'sortValue': sortValue,
      'filterValue': filterValue
    }
    axios.get(prize_list, { headers, params }).then(response => {
      setData(response.data.prize_list_with_game);
      setFilter_options(response.data.organization)
    });

    // filterValue
    // sortValue
    // request
    // setData(...)
  }


  return (
    <div style={body_div}>
      <Header selectedTab={3} showEditorTab={false} />
      <div style={prizes_block}>
        <div style={sort_block}>
          <Title style={{ margin: "0" }} level={5}>Сортировка: </Title>
          <Select style={{ flexGrow: 1 }} placeholder="Организации" options={filter_options} onChange={value => setFilterValue(value)} />
          <Select style={{ flexGrow: 1 }} placeholder="Сортировка" options={[{ label: 'По алфавиту', value: "alphabet", }, { label: "Против алфавита", value: "reverse" }]} onChange={value => setSortValue(value)} />
          <Button onClick={refresh} type="primary">Обновить</Button>
        </div>
        <List style={full_width} itemLayout="horizontal" dataSource={data} renderItem={(item, index) => (
          <List.Item style={full_width}>
            <Card bodyStyle={card_body} style={full_width} title={item.name} extra={<p>{item.owner} : <a href={"/user/game/" + item.fieldId}>{item.fieldName}</a></p>}>
              <Image style={image} width={200} height={200} src={item.avatar}  fallback={img_fallback} />
              <Paragraph >{item.description}</Paragraph>
            </Card>
          </List.Item>
        )} />
      </div>
    </div>
  );
}


export default MyPrizes;
